import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const InvoiceForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [invoice, setInvoice] = useState({
    customerName: '',
    items: []
  });

  useEffect(() => {
    fetchCategories();
    if (id) {
      fetchInvoice();
    }
  }, [id]);
  const fetchInvoice = async () => {
    try {
      const response = await axios.get(`/invoices/getInvoice/${id}`);
      setInvoice(response.data);
      // If there are products in the invoice, fetch the category
      if (response.data.items.length > 0) {
        const firstProduct = await axios.get(`/products/getProducts/${response.data.items[0].product}`);
        setSelectedCategory(firstProduct.data.partType);
        fetchProducts(firstProduct.data.partType);
      }
    } catch (error) {
      console.error('Error fetching invoice:', error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get('/products/getProducts');
      const uniqueCategories = [...new Set(response.data.map(product => product.partType))];
      setCategories(uniqueCategories);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchProducts = async (category) => {
    try {
      const response = await axios.get(`/products/getProducts?category=${category}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (id) {
        await axios.put(`/invoices/updateInvoice/${id}`, invoice);
      } else {
        await axios.post('/invoices/addInvoice', invoice);
      }
      navigate('/invoice');
    } catch (error) {
      console.error('Error saving invoice:', error);
    }
  };

  const addItem = () => {
    setInvoice(prev => ({
      ...prev,
      items: [...prev.items, { product: '', quantity: 1, priceType: 'single' }]
    }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Customer Name</label>
        <input
          type="text"
          className="form-control"
          value={invoice.customerName}
          onChange={(e) => setInvoice(prev => ({ ...prev, customerName: e.target.value }))}
          required
        />
      </div>

      <div className="items-section">
        <h3>Items</h3>
        {invoice.items.map((item, index) => (
          <div key={index} className="item-row">
            <select
              value={selectedCategory}
              onChange={(e) => {
                setSelectedCategory(e.target.value);
                fetchProducts(e.target.value);
              }}
            >
              <option value="">Select Category</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>

            <select
              value={item.product}
              onChange={(e) => {
                const items = [...invoice.items];
                items[index].product = e.target.value;
                setInvoice(prev => ({ ...prev, items }));
              }}
            >
              <option value="">Select Product</option>
              {products.map(product => (
                <option key={product._id} value={product._id}>{product.partNumber}</option>
              ))}
            </select>

            <input
              type="number"
              value={item.quantity}
              onChange={(e) => {
                const items = [...invoice.items];
                items[index].quantity = parseInt(e.target.value);
                setInvoice(prev => ({ ...prev, items }));
              }}
              min="1"
            />

            <select
              value={item.priceType}
              onChange={(e) => {
                const items = [...invoice.items];
                items[index].priceType = e.target.value;
                setInvoice(prev => ({ ...prev, items }));
              }}
            >
              <option value="single">Single Price</option>
              <option value="bulk">Bulk Price</option>
            </select>

            <button
              type="button"
              onClick={() => {
                const items = invoice.items.filter((_, i) => i !== index);
                setInvoice(prev => ({ ...prev, items }));
              }}
            >
              Remove
            </button>
          </div>
        ))}
        <button type="button" onClick={addItem}>Add Item</button>
      </div>

      <button type="submit" className="btn btn-primary">
        {id ? 'Update Invoice' : 'Create Invoice'}
      </button>
    </form>
  );
};

export default InvoiceForm;
