import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const InvoiceList = () => {
  const [invoices, setInvoices] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchInvoices();
  }, [search]);

  const fetchInvoices = async () => {
    try {
      const response = await axios.get(`/invoices/getInvoices?search=${search}`);
      setInvoices(response.data);
    } catch (error) {
      console.error('Error fetching invoices:', error);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this invoice?')) {
      try {
        await axios.delete(`/invoices/deleteInvoice/${id}`);
        fetchInvoices();
      } catch (error) {
        console.error('Error deleting invoice:', error);
      }
    }
  };

  const handleDownloadPDF = async (invoiceId) => {
    try {
      const response = await axios.get(`/invoice/${invoiceId}/pdf`, {
        responseType: 'blob',
      });

      const file = new Blob([response.data], { type: 'application/pdf' });
      
      const fileURL = URL.createObjectURL(file);
      const link = document.createElement('a');
      link.href = fileURL;
      link.download = `invoice-${invoiceId}.pdf`;
      link.click();
      
      URL.revokeObjectURL(fileURL);
    } catch (error) {
      console.error('Error downloading PDF:', error);
      alert('Error downloading PDF');
    }
  };

  return (
    <div>
      <h2>Invoices</h2>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search invoices..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>
      <Link to="/InvoiceForm" className="btn btn-primary">Create New Invoice</Link>
      <table className="table">
        <thead>
          <tr>
            <th>Customer Name</th>
            <th>Date</th>
            <th>Total Amount</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {invoices.map((invoice) => (
            <tr key={invoice._id}>
              <td>{invoice.customerName}</td>
              <td>{new Date(invoice.date).toLocaleDateString()}</td>
              <td>${invoice.totalAmount.toFixed(2)}</td>
              <td>
                <Link to={`/invoices/updateInvoice/${invoice._id}`} className="btn btn-sm btn-info">Edit</Link>
                <button onClick={() => handleDelete(invoice._id)} className="btn btn-sm btn-danger">Delete</button>
                <button 
                  onClick={() => handleDownloadPDF(invoice._id)} 
                  className="btn btn-sm btn-secondary"
                >
                  Download PDF
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InvoiceList;
